/* * Lookup table used to maintain the user selected Accent Color upon Dark/Light toggle. * Matches the "alfred_theme_background" variable results for each theme with their corresponding Accent Colors. */
var accentColorLookup = {
// Light Themes
"rgba(252,253,255,0.84)": "Blue", // #FCFDFFD6 "rgba(254,252,255,0.84)": "Purple", // #FEFCFFD6 "rgba(255,252,253,0.84)": "Pink", // #FFFCFDD6 "rgba(255,252,252,0.84)": "Red", // #FFFCFCD6 "rgba(255,253,252,0.84)": "Orange", // #FFFDFCD6 "rgba(255,254,252,0.84)": "Yellow", // #FFFEFCD6 "rgba(252,255,252,0.84)": "Green", // #FCFFFCD6 "rgba(255,255,255,0.84)": "Gray", // #FFFFFFD6
// Dark Themes
"rgba(50,51,53,0.77)": "Blue", // #323335C4 "rgba(52,50,53,0.77)": "Purple", // #343235C4 "rgba(53,50,51,0.77)": "Pink", // #353233C4 "rgba(53,50,50,0.77)": "Red", // #353232C4 "rgba(53,51,50,0.77)": "Orange", // #353332C4 "rgba(53,52,50,0.77)": "Yellow", // #353432C4 "rgba(50,53,50,0.77)": "Green", // #323532C4 "rgba(53,53,53,0.77)": "Gray", // #353535C4
}
function run(args) {
args = args ? args : []
systemEvents = Application("System Events")
alfredApp = Application("Alfred 3")
ObjC.import('stdlib') actualTheme = $.getenv('alfred_theme_background')
actualAccent = accentColorLookup[actualTheme]
isDark = systemEvents.appearancePreferences.darkMode()
actualMode = isDark ? 'Dark' : 'Light' reverseMode = isDark ? 'Light' : 'Dark'
// Arg is used to change Accent Color.
if (args.length !== 0) { alfredApp.setTheme("Mojave " + actualMode + " - " + args) }
// Without args, toggles between Dark & Light mode.
else { systemEvents.appearancePreferences.darkMode = !systemEvents.appearancePreferences.darkMode() alfredApp.setTheme("Mojave " + reverseMode + " - " + actualAccent) }
}
